mapper.select:
SELECT product_id, product_name, price, stock, created_time, updated_time
FROM product
WHERE status = 1 AND product_id = #{productId}

mapper.update:
UPDATE product

    product_name = #{productName}
    price = #{price}
    stock = #{stock}

WHERE product_id = #{productId}

mapper.delete:
UPDATE product
SET status = 0
WHERE product_id = #{productId}
